<?phpfunction getAssignedCatNames($pageID){    $namearray = array();    foreach (get_the_category($pageID) as $cat)    {        $namearray[] = strtolower(str_replace(' ', '-', $cat->slug));    }    return implode(' ', $namearray);}function getAssignedCatIDs($pageID){    $catarray = array();    foreach (get_the_category($pageID) as $cat)    {        if (is_category())        {            if (get_query_var('cat') == $cat->cat_ID)                $catarray[] = $cat->cat_ID;        }        else            $catarray[] = $cat->cat_ID;    }    return implode(', ', $catarray);}function getCatsNotAssignedTo($pageID){    $catarray = array();    foreach (get_the_category($pageID) as $cat)    {        if (is_category())        {            if (get_query_var('cat') == $cat->cat_ID)                $catarray[] = $cat->cat_ID;        }        else            $catarray[] = $cat->cat_ID;    }    return implode(' and ', array_diff(get_all_category_ids(), $catarray));}function var_dump_pre($obj){    ?>    <pre style="width: 100%; float: left;">        <?php print_r($obj); ?>    </pre>    <?php}function popupText($text = 'popupText'){    ?>    <script type="text/javascript">        alert('<?php echo $text; ?>');    </script>    <?php}function currentTemplateFile(){    return get_post_meta(get_the_ID(), '_wp_page_template', TRUE);}function isCurrentTemplateFile($file){    return $file == currentTemplateFile();}function showTitle($title, $classes = '', $showdate = false, $hyperlink = false, $headertag = 'h1'){    ?>    <div class ='titleheader <?php echo $classes; ?>'>        <<?php echo $headertag; ?> class="title">        <?php        if ($hyperlink)            echo "<a href='" . get_the_permalink() . "'>";        echo $title;        if ($hyperlink)            echo '</a>'            ?>        </<?php echo $headertag; ?>>        <?php if ($showdate): ?>            <span class="postinfo"><?php echo get_the_date(); ?></span>        <?php endif; ?>    </div>    <?php}function loadFunctions($root = 'functions'){    $path = get_template_directory() . '/' . $root;    $results = scandir($path);    foreach ($results as $result)    {        if ($result === '.' or $result === '..')            continue;        $file = $root . '/' . $result . '/' . $result . '.php';        $fullpath = get_template_directory() . '/' . $file;        if (is_dir($path . '/' . $result))            if (file_exists($fullpath))                require_once $fullpath;    }}