<?phpdefine('SIDEBAR_SINGLE_LEFT', 'Single Sidebar Left');define('SIDEBAR_FOOTER_ONE', 'Footer Sidebar One');define('SIDEBAR_FOOTER_TWO', 'Footer Sidebar Two');define('SIDEBAR_FOOTER_THREE', 'Footer Sidebar Three');define('SIDEBAR_FOOTER_FOUR', 'Footer Sidebar Four');function registerSidebars(){    register_sidebar(            array(                'id' => 'sidebar_single_left',                'name' => SIDEBAR_SINGLE_LEFT,                'before_widget' => '<ul><li id="%1$s" class="widget %2$s">',                'after_widget' => '</li></ul>',                'before_title' => '<h2 class="sidebar_title">',                'after_title' => '</h2>',            )    );    register_sidebar(            array(                'id' => 'sidebar_footer_one',                'name' => SIDEBAR_FOOTER_ONE,                'before_widget' => '<ul><li id="%1$s" class="widget %2$s">',                'after_widget' => '</li></ul>',                'before_title' => '<h2 class="sidebar_title">',                'after_title' => '</h2>',            )    );    register_sidebar(            array(                'id' => 'sidebar_footer_two',                'name' => SIDEBAR_FOOTER_TWO,                'before_widget' => '<ul><li id="%1$s" class="widget %2$s">',                'after_widget' => '</li></ul>',                'before_title' => '<h2 class="sidebar_title">',                'after_title' => '</h2>',            )    );    register_sidebar(            array(                'id' => 'sidebar_footer_three',                'name' => SIDEBAR_FOOTER_THREE,                'before_widget' => '<ul><li id="%1$s" class="widget %2$s">',                'after_widget' => '</li></ul>',                'before_title' => '<h2 class="sidebar_title">',                'after_title' => '</h2>',            )    );    register_sidebar(            array(                'id' => 'sidebar_footer_four',                'name' => SIDEBAR_FOOTER_FOUR,                'before_widget' => '<ul><li id="%1$s" class="widget %2$s">',                'after_widget' => '</li></ul>',                'before_title' => '<h2 class="sidebar_title">',                'after_title' => '</h2>',            )    );}add_action('init', 'registerSidebars');function isSidebarUsed($name){    ob_start();    dynamic_sidebar($name);    $used = ob_get_contents();    ob_clean();    return $used;}function createSidebar($sidebarname, $class = ''){    ?>        <div class="<?php echo $class; ?>">        <?php dynamic_sidebar($sidebarname); ?>    </div>    <?php}function sidebarStyleSetup(){    wp_enqueue_style('sidebarStyle', get_template_directory_uri() . '/functions/sidebar/sidebar.css');}add_action('wp_enqueue_scripts', 'sidebarStyleSetup', 1);