<?phpfunction setupHeader(){    $customHeaderArgs = array(        'default-image' => get_template_directory_uri() . '/images/header.jpg',        'random-default' => FALSE,        'width' => 500,        'height' => 155,        'flex-height' => TRUE,        'flex-width' => TRUE,        'default-text-color' => '',        'header-text' => FALSE,        'uploads' => TRUE,        'wp-head-callback' => '',        'admin-head-callback' => '',        'admin-preview-callback' => ''    );    add_theme_support('custom-header', $customHeaderArgs);}add_action('init', 'setupHeader');//http://codex.wordpress.org/Custom_Backgroundsfunction setupcustombackground(){    $defaults = array(        'default-color' => '',        'default-image' => '',        'wp-head-callback' => '_custom_background_cb',        'admin-head-callback' => '',        'admin-preview-callback' => ''    );    add_theme_support('custom-background', $defaults);}add_action('init', 'setupcustombackground');if (!isset($content_width))    $content_width = 755;function my_theme_add_all_editor_styles(){    add_editor_style('./style.css');}add_action('init', 'my_theme_add_all_editor_styles');//http://www.wprecipes.com/how-to-remove-the-width-and-height-attributes-from-wp-image-uploaderfunction remove_width_attribute($html){    $html = preg_replace('/(width|height)="\d*"\s/', "", $html);    return $html;}add_filter('post_thumbnail_html', 'remove_width_attribute', 10);add_filter('image_send_to_editor', 'remove_width_attribute', 10);//http://blog.iamcreative.me/code-snippets/remove-phantom-paragraph-tags-iframes-images-wordpress/#.Uw-Bkl6cfxg//http://css-tricks.com/snippets/wordpress/remove-paragraph-tags-from-around-images/function filter_ptags_on_images($content){    $content = preg_replace('/<p>\s*(<a .*>)?\s*(<img .* \/>)\s*(<\/a>)?\s*<\/p>/iU', '\1\2\3', $content);    $content = preg_replace('/<p>\s*(<iframe .*>*.<\/iframe>)\s*<\/p>/iU', '\1', $content);    $content = preg_replace('/<p>\s*(<script .*>*.<\/script>)\s*<\/p>/iU', '\1', $content);    return $content;}add_filter('the_content', 'filter_ptags_on_images');function stylesSetup(){    wp_enqueue_style('normalize', get_template_directory_uri() . '/css/normalize.css');    //wp_enqueue_style('blocknormalize', get_template_directory_uri() . '/css/blocknormalize.css');    wp_enqueue_style('style', get_stylesheet_uri());    wp_enqueue_style('responsive', get_template_directory_uri() . '/css/responsive.css');}add_action('wp_enqueue_scripts', 'stylesSetup', 500);//http://webdesign.tutsplus.com/articles/quick-tip-dont-forget-the-viewport-meta-tag--webdesign-5972 //<meta name="viewport" content="initial-scale=1">////http://stackoverflow.com/questions/16470801/inconsistent-font-sizes-on-smart-phone //<meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1">function myhead(){    ?>    <title>         <?php        wp_title('&laquo;', true, 'right');        bloginfo('name');        ?>     </title>    <link rel="icon" type="image/png" href="<?php echo get_template_directory_uri(); ?>/favicon.png">    <meta http-equiv="Content-type" content="text/html;charset=UTF-8">    <meta name="viewport" content="initial-scale=1">    <?php}add_action('wp_head', 'myhead');function local_title($title){    if ($_SERVER['SERVER_ADDR'] === '127.0.0.1')        $title = 'LOCAL - ' . $title;    return $title;}add_filter('wp_title', 'local_title', 10, 2);add_filter('admin_title', 'local_title', 10, 2);// Remove that junk from my wp_headremove_action('wp_head', 'rsd_link'); // Removes the Really Simple Discovery linkremove_action('wp_head', 'wlwmanifest_link'); // Removes the Windows Live Writer linkremove_action('wp_head', 'wp_generator'); // Removes the WordPress versionremove_action('wp_head', 'feed_links', 2); // Removes the RSS feeds remember to add post feed maunally (if required) to header.phpremove_action('wp_head', 'feed_links_extra', 3); // Removes all other RSS linksremove_action('wp_head', 'index_rel_link'); // Removes the index page linkremove_action('wp_head', 'start_post_rel_link', 10, 0); // Removes the random post linkremove_action('wp_head', 'parent_post_rel_link', 10, 0); // Removes the parent post linkremove_action('wp_head', 'adjacent_posts_rel_link_wp_head', 10, 0); // Removes the next and previous post linksremove_action('wp_head', 'rel_canonical', 10, 0); // Removes canonical linksremove_action('wp_head', 'wp_shortlink_wp_head', 10, 0); // Removes shortlink