<?php/*  Template Name: First Post In Category */query_posts(array('showposts' => 1, 'cat' => getAssignedCatIDs(get_the_ID()))); //gets first post in all categories assigned to this pageif (have_posts()) : while (have_posts()) : the_post();        $url = get_permalink();        wp_redirect($url, 301);        wp_reset_query();        return;    endwhile;else:    wp_redirect(home_url(), 301); //no posts in categories, it goes home    wp_reset_query();endif;